import React, { useState } from 'react';

function SalaryCalculator() {
  const [grossSalary, setGrossSalary] = useState(0);

  function calculateAfterTaxSalary() {
    let afterTaxSalary;

    if (grossSalary <= 12570) {
      afterTaxSalary = grossSalary;
    } else if (grossSalary <= 50270) {
      afterTaxSalary = 12570 + (grossSalary - 12570) * 0.8;
    } else if (grossSalary <= 150000) {
      afterTaxSalary = 12570 + (50270 - 12570) * 0.8 + (grossSalary - 50270) * 0.6;
    } else {
      afterTaxSalary = 12570 + (50270 - 12570) * 0.8 + (150000 - 50270) * 0.6 + (grossSalary - 150000) * 0.55;
    }

    setGrossSalary(afterTaxSalary.toFixed(2));
  }  

  return (
    <div>
      <h1>UK After-Tax Salary Calculator</h1>
      <input type="number" value={grossSalary} onChange={(e) => setGrossSalary(e.target.value)} />
      <button onClick={calculateAfterTaxSalary}>Calculate</button>
    </div>
  );
}

export default SalaryCalculator;
